import androidx.compose.runtime.saveable.Saver
import androidx.compose.runtime.saveable.rememberSaveable
import com.bumble.appyx.core.node.Node
import com.bumble.appyx.core.node.NodeBuilder
import com.bumble.appyx.core.node.NodeHostState
import com.bumble.appyx.core.node.NodeHostStateImpl

@Composable
fun rememberNode(
    nodeBuilder: NodeBuilder,
    key: String? = null,
    restore: Boolean = true,
): Node {
    val nodeHostState = LocalNodeHostState.current
    return remember(key) {
        nodeHostState.rememberNode(
            nodeBuilder = nodeBuilder,
            key = key,
            restore = restore
        )
    }
}

@Composable
fun NodeHost(
    nodeBuilder: NodeBuilder,
    key: String? = null,
    restore: Boolean = true,
    content: @Composable (Node) -> Unit,
) {
    val node = rememberNode(nodeBuilder, key, restore)
    content(node)
}

