import io.micrometer.core.instrument.Timer
import org.springframework.util.StringUtils
import java.util.concurrent.TimeUnit

/**
 * Utility class for GraphQL instrumentation.
 */
object DgsGraphQLMetricsInstrumentationUtils {

    /**
     * Creates a [Timer.Sample] and starts the timer.
     *
     * @param meterRegistry The registry to register the timer with.
     * @param meterName The name of the meter to create.
     * @param tags The tags to apply to the timer.
     * @return A [Timer.Sample] that can be used to stop the timer.
     */
    fun startTimer(
        meterRegistry: MeterRegistry,
        meterName: String,
        tags: Iterable<Tag>
    ): Timer.Sample {
        return Timer
            .builder(meterName)
            .tags(tags)
            .register(meterRegistry)
            .start()
    }

    /**
     * Stops the timer and records the elapsed time.
     *
     * @param sample The [Timer.Sample] to stop.
     * @param meterRegistry The registry to record the timer with.
     * @param meterName The name of the meter to record.
     * @param tags The tags to apply to the timer.
     */
    fun stopTimer(
        sample: Timer.Sample,
        meterRegistry: MeterRegistry,
        meterName: String,
        tags: Iterable<Tag>
    ) {
        sample.stop(
            Timer
                .builder(meterName)
                .tags(tags)
                .register(meterRegistry)
        )
    }

    /**
     * Extracts the field name from a GraphQL path.
     *
     * @param path The GraphQL path to extract the field name from.
     * @return The field name extracted from the path.
     */
    fun extractFieldNameFromPath(path: String): String {
        return path.substringAfterLast('.')
    }

    /**
     * Extracts the parent type from a GraphQL path.
     *
     * @param path The GraphQL path to extract the parent type from.
     * @return The parent type extracted from the path.
     */
    fun extractParentTypeFromPath(path: String): String {
        return path.substringBeforeLast('.')
    }

    /**
     * Sanitizes a GraphQL path by removing any non-alphanumeric characters.
     *
     * @param path The GraphQL path to sanitize.
     * @return The sanitized GraphQL path.
     */
    fun sanitizePath(path: String): String {
        return if (StringUtils.hasText(path)) {
            path.replace(Regex("[^a-zA-Z0-9.\\[\\]]"), "_")
        } else {
            "unknown"
        }
    }
}

