import com.fsck.k9.Account
import com.fsck.k9.backend.api.Backend
import com.fsck.k9.backend.api.FolderInfo
import com.fsck.k9.backend.api.FolderType
import com.fsck.k9.controller.MessagingController
import com.fsck.k9.helper.Utility
import com.fsck.k9.mailstore.LockableDatabase.DbCallback
import com.fsck.k9.mailstore.LockableDatabase.Transaction
import timber.log.Timber

class SpecialFolderUpdater(
    private val account: Account,
    private val backend: Backend,
    private val messagingController: MessagingController
) {
    fun updateSpecialFolders() {
        try {
            val folderInfos = backend.getFolderList()
            val specialFolders = getSpecialFolders(folderInfos)

            if (specialFolders.isNotEmpty()) {
                updateSpecialFolderSettings(specialFolders)
            }
        } catch (e: Exception) {
            Timber.e(e, "Error while updating special folders for account %s", account.uuid)
        }
    }

    private fun getSpecialFolders(folderInfos: List<FolderInfo>): Map<FolderType, String> {
        val specialFolders = mutableMapOf<FolderType, String>()

        for (folderInfo in folderInfos) {
            when (folderInfo.type) {
                FolderType.INBOX -> specialFolders[FolderType.INBOX] = folderInfo.serverId
                FolderType.DRAFTS -> specialFolders[FolderType.DRAFTS] = folderInfo.serverId
                FolderType.SENT -> specialFolders[FolderType.SENT] = folderInfo.serverId
                FolderType.TRASH -> specialFolders[FolderType.TRASH] = folderInfo.serverId
                FolderType.SPAM -> specialFolders[FolderType.SPAM] = folderInfo.serverId
                FolderType.ARCHIVE -> specialFolders[FolderType.ARCHIVE] = folderInfo.serverId
                else -> {
                    // Ignore other folders
                }
            }
        }

        return specialFolders
    }

    private fun updateSpecialFolderSettings(specialFolders: Map<FolderType, String>) {
        account.draftsFolder = specialFolders[FolderType.DRAFTS]
        account.sentFolder = specialFolders[FolderType.SENT]
        account.trashFolder = specialFolders[FolderType.TRASH]
        account.spamFolder = specialFolders[FolderType.SPAM]
        account.archiveFolder = specialFolders[FolderType.ARCHIVE]

        // Only update inbox if it's not set yet or if we're in a state where we can update it
        if (account.inboxFolder == null) {
            account.inboxFolder = specialFolders[FolderType.INBOX]
        }
    }
}

