file content: core/src/main/java/com/google/android/fhir/db/impl/dao/ResourceDao.kt: package com.google.android.fhir.db.impl.dao

import androidx.room.*
import com.google.android.fhir.db.impl.entities.ResourceEntity
import com.google.android.fhir.db.impl.entities.ResourceEntityWithIndex
import org.hl7.fhir.r4.model.Resource

@Dao
interface ResourceDao {
  @Insert(onConflict = OnConflictStrategy.REPLACE)
  suspend fun insert(resource: ResourceEntity)

  @Update
  suspend fun update(resource: ResourceEntity)

  @Query("SELECT * FROM resource WHERE resourceType = :resourceType AND id = :id")
  suspend fun get(resourceType: String, id: String): ResourceEntity?

  @Query("SELECT * FROM resource WHERE resourceType = :resourceType AND id = :id")
  suspend fun get(resourceType: String, id: String): ResourceEntityWithIndex?

  @Query("DELETE FROM resource WHERE resourceType = :resourceType AND id = :id")
  suspend fun delete(resourceType: String, id: String)

  @Query("SELECT * FROM resource WHERE resourceType = :resourceType")
  suspend fun getAll(resourceType: String): List<ResourceEntity>

  @Query("SELECT * FROM resource")
  suspend fun getAll(): List<ResourceEntity>
}

