import io.micrometer.core.instrument.Timer
import org.slf4j.LoggerFactory
import java.util.concurrent.CompletableFuture

/**
 * Utility class for creating and recording metrics.
 */
object DgsGraphQLMetricsInstrumentationUtils {

    private val logger = LoggerFactory.getLogger(DgsGraphQLMetricsInstrumentationUtils::class.java)

    /**
     * Records the timer for a given metric.
     *
     * @param timer The timer to record.
     * @param metric The metric to record.
     * @param tags The tags to apply to the metric.
     */
    fun recordTimer(timer: Timer, metric: DgsMetrics.GqlMetric, tags: Map<String, String>) {
        try {
            logger.debug("Recording timer for metric {} with tags {}", metric.key, tags)
            timer.record {
                // No-op - just recording the timer
            }
        } catch (ex: Exception) {
            logger.warn("Failed to record timer for metric {} with tags {}", metric.key, tags, ex)
        }
    }

    /**
     * Records a timer for a given metric.
     *
     * @param timer The timer to record.
     * @param metric The metric to record.
     * @param tags The tags to apply to the metric.
     * @return A completable future that completes when the timer is recorded.
     */
    fun recordTimer(timer: CompletableFuture<Timer>, metric: DgsMetrics.GqlMetric, tags: Map<String, String>): CompletableFuture<Void> {
        return timer.thenCompose { t ->
            try {
                logger.debug("Recording timer for metric {} with tags {}", metric.key, tags)
                t.record {
                    // No-op - just recording the timer
                }
                CompletableFuture.completedFuture(null)
            } catch (ex: Exception) {
                logger.warn("Failed to record timer for metric {} with tags {}", metric.key, tags, ex)
                CompletableFuture.failedFuture(ex)
            }
        }
    }
}

