public class EventTypeService {

    private final EventTypeRepository eventTypeRepository;
    private final EventTypeValidator eventTypeValidator;
    private final EventTypeNameValidator eventTypeNameValidator;
    private final TimelineService timelineService;
    private final PartitionStrategyFactory partitionStrategyFactory;
    private final EnrichmentManager enrichmentManager;

    public EventTypeService(final EventTypeRepository eventTypeRepository,
                            final EventTypeValidator eventTypeValidator,
                            final EventTypeNameValidator eventTypeNameValidator,
                            final TimelineService timelineService,
                            final PartitionStrategyFactory partitionStrategyFactory,
                            final EnrichmentManager enrichmentManager) {
        this.eventTypeRepository = eventTypeRepository;
        this.eventTypeValidator = eventTypeValidator;
        this.eventTypeNameValidator = eventTypeNameValidator;
        this.timelineService = timelineService;
        this.partitionStrategyFactory = partitionStrategyFactory;
        this.enrichmentManager = enrichmentManager;
    }

    public EventType create(final EventType eventType) throws EventTypeAlreadyExistsException {
        // validation
        eventTypeValidator.validate(eventType);
        eventTypeNameValidator.validate(eventTypeNameValidator, eventType.getName());

        // persistence
        final EventType createdEventType = eventTypeRepository.create(eventType);

        // timeline creation
        timelineService.createTimeline(eventType);

        return createdEventType;
    }

    public void update(final String eventType, final EventTypePatch patch) throws EventTypeNotFoundException {
        final EventType original = eventTypeRepository.findByName(eventType);
        final EventType patched = patch.apply(original);

        eventTypeValidator.validate(patched);
        eventTypeRepository.update(patched);
    }

    public void delete(final String eventType) throws EventTypeNotFoundException {
        eventTypeRepository.delete(eventType);
    }

    public EventType getEventType(final String name) throws EventTypeNotFoundException {
        return eventTypeRepository.findByName(name);
    }
}

