import sys
import json
import time
import threading
from typing import Any, Dict, Optional  # noqa

from chalice.constants import (CHALICE_FOLDER, CHALICE_CONFIG_FILE,
                               CHALICE_LIB_DIR)
from chalice.compat import subprocess_python_base_environ


def get_chalice_app_dir():
    # type: () -> str
    return os.path.join(os.getcwd(), CHALICE_FOLDER)


def get_project_config():
    # type: () -> Dict[str, Any]
    config_file = os.path.join(get_chalice_app_dir(), CHALICE_CONFIG_FILE)
    with open(config_file) as f:
        return json.loads(f.read())


class OSUtils(object):
    def __init__(self):
        # type: () -> None
        self._subprocess_env = None  # type: Optional[Dict[str, str]]

    def _get_subprocess_env(self):
        # type: () -> Dict[str, str]
        if self._subprocess_env is None:
            self._subprocess_env = os.environ.copy()
        return self._subprocess_env

    def run_command(self, command, shell=False, env=None):
        # type: (List[str], bool, Optional[Dict[str, str]]) -> str
        if env is None:
            env = self._get_subprocess_env()
        return subprocess.check_output(
            command, shell=shell, env=env, stderr=subprocess.STDOUT)

    def create_process(self, command, shell=False, env=None):
        # type: (List[str], bool, Optional[Dict[str, str]]) -> Any
        if env is None:
            env = self._get_subprocess_env()
        return subprocess.Popen(
            command, shell=shell, env=env)

    def get_pip_install_command(self, package_name, target_dir):
        # type: (str, str) -> List[str]
        return [sys.executable, '-m', 'pip', 'install',
                '--target', target_dir, package_name]

