/**
 * Inspection for abbreviations that are not followed by a space.
 *
 * @author Hannah Schellekens
 */
class LatexSpaceAfterAbbreviationInspection : TexifyInspectionBase() {

    override val inspectionGroup = InsightGroup.LATEX

    override fun getDisplayName() = "Missing space after abbreviation"

    override val inspectionId = "SpaceAfterAbbreviation"

    override fun inspectFile(file: PsiFile, manager: InspectionManager, isOntheFly: Boolean): List<ProblemDescriptor> {
        val descriptors = descriptorList()
        val document = file.document() ?: return descriptors

        // Target all regular text for this inspection.
        val texts = file.childrenOfType(LatexNormalText::class)
        for (text in texts) {
            // Do not trigger the inspection in math mode.
            if (text.inMathContext()) {
                continue
            }

            val matcher = PatternMagic.abbreviation.matcher(text.text)
            while (matcher.find()) {
                val offset = text.textOffset + matcher.end()
                val lineNumber = document.getLineNumber(offset)
                val endLine = document.getLineEndOffset(lineNumber)

                val startOffset = matcher.start()
                val endOffset = matcher.end() + (endLine - offset)

                val element = file.findElementAt(startOffset + text.startOffset)
                // Do not trigger the inspection when in a comment or when a comment starts directly after.
                if ((element?.isComment() == true) || (element?.nextSiblingIgnoreWhitespace()?.isComment() == true)) {
                    continue
                }

                val fixes = listOf(InspectionFix()).toTypedArray()
                descriptors.add(manager.createProblemDescriptor(
                        text,
                        startOffset,
                        endOffset,
                        ProblemHighlightType.GENERIC_ERROR_OR_WARNING,
                        isOntheFly,
                        *fixes
                ))
            }
        }

        return descriptors
    }

    /**
     * Fix for adding a space after an abbreviation.
     */
    class NormalSpaceFix(private val range: IntRange) : LocalQuickFix {

        override fun getFamilyName() = "Add space after abbreviation"

        override fun applyFix(project: Project, descriptor: ProblemDescriptor) {
            val element = descriptor.psiElement
            if (element is LatexNormalText) {
                val text = element.text
                val newText = text.substring(0, range.first) + " " + text.substring(range.first)
                element.replaceWithText(newText)
            }
        }
    }

    /**
     * Fix for adding a space after an abbreviation.
     */
    class InspectionFix : LocalQuickFix {

        override fun getFamilyName() = "Add space after abbreviation"

        override fun applyFix(project: Project, descriptor: ProblemDescriptor) {
            val element = descriptor.psiElement
            if (element is LatexNormalText) {
                val text = element.text
                val newText = text.substring(0, range.first) + " " + text.substring(range.first)
                element.replaceWithText(newText)
            }
        }
    }
}

