            locale: Locale
        ): DateFormatter = DateFormatter(
            createNsFormatter(style, locale),
            "",
            timeZone
        )

        actual fun dateTimeFormat(
            dateStyle: DateFormatStyle,
            timeStyle: DateFormatStyle,
            timeZone: TimeZone,
            locale: Locale
        ): DateFormatter = DateFormatter(
            createNsFormatter(dateStyle, timeStyle, locale),
            "",
            timeZone
        )

        actual fun patternFormat(pattern: String, timeZone: TimeZone, locale: Locale): DateFormatter {
            val nsFormatter = NSDateFormatter()
            nsFormatter.dateFormat = pattern
            nsFormatter.timeZone = NSTimeZone.timeZoneWithName(timeZone.identifier) as? NSTimeZone
            return DateFormatter(nsFormatter, pattern, timeZone)
        }
    }

    actual var pattern: String = pattern

    actual var timeZone: TimeZone = timeZone
        set(value) {
            field = value
            nsFormatter.timeZone = NSTimeZone.timeZoneWithName(value.identifier) as? NSTimeZone
        }

    actual var eras: List<String> = emptyList()
    actual var months: List<String> = emptyList()
    actual var shortMonths: List<String> = emptyList()
    actual var weekdays: List<String> = emptyList()
    actual var shortWeekdays: List<String> = emptyList()
    actual var amString: String = ""
    actual var pmString: String = ""

    actual fun format(date: Date): String {
        return nsFormatter.stringFromDate(date.date)
    }

    actual fun parse(string: String): Date? {
        val date = nsFormatter.dateFromString(string)
        return if (date != null) {
            Date(date, timeZone, Locale.defaultLocale)
        } else {
            null
        }
    }

    private companion object {
        private fun createNsFormatter(style: DateFormatStyle, locale: Locale): NSDateFormatter {
            val formatter = NSDateFormatter()
