            formatter.timeZone = NSTimeZone.defaultTimeZone()
            formatter.locale = NSLocale(locale.languageCode, locale.countryCode)
            return formatter
        }

        private fun createNsFormatter(
            dateStyle: DateFormatStyle,
            timeStyle: DateFormatStyle,
            locale: Locale
        ): NSDateFormatter {
            val formatter = NSDateFormatter()
            formatter.dateStyle = dateStyle.nsStyle()
            formatter.timeStyle = timeStyle.nsStyle()
            formatter.timeZone = NSTimeZone.defaultTimeZone()
            formatter.locale = NSLocale(locale.languageCode, locale.countryCode)
            return formatter
        }
