import com.fsck.k9.mail.FetchProfile
import com.fsck.k9.mail.Flag
import com.fsck.k9.mail.Folder
import com.fsck.k9.mail.Message
import com.fsck.k9.mail.MessageRetrievalListener
import com.fsck.k9.mail.MessagingException
import com.fsck.k9.mail.Part
import com.fsck.k9.mail.store.imap.ImapResponseParser.Response
import com.fsck.k9.mail.store.imap.commands.AppendCommand
import com.fsck.k9.mail.store.imap.commands.CopyCommand
import com.fsck.k9.mail.store.imap.commands.FetchCommand
import com.fsck.k9.mail.store.imap.commands.ListCommand
import com.fsck.k9.mail.store.imap.commands.SearchCommand
import com.fsck.k9.mail.store.imap.commands.StoreCommand

class ImapFolder(
    private val imapStore: ImapStore,
    private val name: String,
    private val delimiter: String
) : Folder {
    override fun getName(): String = name

    override fun getServerId(): String = name

    override fun getDelimiter(): String = delimiter

    override fun exists(): Boolean {
        return try {
            open(OPEN_MODE_RO)
            true
        } catch (e: MessagingException) {
            false
        }
    }

    override fun open(mode: Int) {
        if (mode == OPEN_MODE_RO) {
            imapStore.openFolder(name, false)
        } else {
            imapStore.openFolder(name, true)
        }
    }

    override fun close() {
        imapStore.closeFolder(name)
    }

    override fun getMessages(start: Int, end: Int, listener: MessageRetrievalListener?): List<Message> {
        val fetchCommand = FetchCommand(imapStore, name, start, end, listener)
        return fetchCommand.fetch()
    }

    override fun getMessages(from: Int, to: Int): List<Message> {
        val fetchCommand = FetchCommand(imapStore, name, from, to)
        return fetchCommand.fetch()
    }

    override fun getMessages(): List<Message> {
        val fetchCommand = FetchCommand(imapStore, name)
        return fetchCommand.fetch()
    }

    override fun search(
        query: String,
        requiredFlags: Set<Flag>?,
        forbiddenFlags: Set<Flag>?
    ): List<Message> {
        val searchCommand = SearchCommand(imapStore, name, query, requiredFlags, forbiddenFlags)
        return searchCommand.search()
    }

    override fun getUidFromMessageId(messageId: String): String? {
        // This is a no-op for IMAP folders
        return messageId
    }

    override fun getMessageCount(): Int {
        return imapStore.getMessageCount(name)
    }

    override fun getUnreadMessageCount(): Int {
        return imapStore.getUnreadMessageCount(name)
    }

    override fun getFlaggedMessageCount(): Int {
        return imapStore.getFlaggedMessageCount(name)
    }

    override fun setFlags(flags: Set<Flag>, set: Boolean) {
        val storeCommand = StoreCommand(imapStore, name, flags, set)
        storeCommand.execute()
    }

    override fun setFlag(messageId: String, flag: Flag, set: Boolean) {
        val storeCommand = StoreCommand(imapStore, name, messageId, flag, set)
        storeCommand.execute()
    }

    override fun copyMessages(messageIds: List<String>, destinationFolder: Folder): List<String> {
        val copyCommand = CopyCommand(imapStore, name, messageIds, destinationFolder)
        return copyCommand.copy()
    }

    override fun appendMessages(messages: List<Message>): List<String> {
        val appendCommand = AppendCommand(imapStore, name, messages)
        return appendCommand.append()
    }

    override fun expunge() {
        imapStore.expunge(name)
    }

    override fun getFolderListing(): List<Folder> {
        val listCommand = ListCommand(imapStore, name)
        return listCommand.list()
    }
}

