file content: app/core/src/main/java/com/fsck/k9/mailstore/SpecialFolderUpdater.kt: package com.fsck.k9.mailstore

import com.fsck.k9.Account
import com.fsck.k9.backend.api.Backend
import com.fsck.k9.controller.MessagingController
import com.fsck.k9.mailstore.SpecialFolderUpdater.FolderUpdateResult
import com.fsck.k9.mailstore.SpecialFolderUpdater.FolderUpdateResult.NoSpecialFoldersFound
import com.fsck.k9.mailstore.SpecialFolderUpdater.FolderUpdateResult.SpecialFoldersUpdated
import com.fsck.k9.mailstore.SpecialFolderUpdater.FolderUpdateResult.SpecialFoldersUpToDate

class SpecialFolderUpdater {
    fun updateSpecialFolders(account: Account, backend: Backend): FolderUpdateResult {
        val specialFolders = backend.getSpecialFolders()
        if (specialFolders == null) {
            return NoSpecialFoldersFound
        }

        val specialFolderIds = account.specialFolderIds
        val updatedSpecialFolderIds = specialFolders.map { it.id }
        if (updatedSpecialFolderIds == specialFolderIds) {
            return SpecialFoldersUpToDate
        }

        account.specialFolderIds = updatedSpecialFolderIds
        return SpecialFoldersUpdated
    }

    sealed class FolderUpdateResult {
        object SpecialFoldersUpToDate : FolderUpdateResult()
        object SpecialFoldersUpdated : FolderUpdateResult()
        object NoSpecialFoldersFound : FolderUpdateResult()
    }
}

