import io.smallrye.context.SmallRyeContextualConsumer
import io.smallrye.context.SmallRyeContextualRunnable
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.launch
import org.jboss.resteasy.reactive.common.core.LazyValue
import org.jboss.resteasy.reactive.server.core.ResteasyReactiveRequestContext
import org.jboss.resteasy.reactive.server.spi.ServerRestHandler

class CoroutineMethodProcessor(
    private val coroutineScope: CoroutineScope,
    private val delegate: ServerRestHandler
) : ServerRestHandler {

    override fun handle(requestContext: ResteasyReactiveRequestContext) {
        if (requestContext.result != null) {
            delegate.handle(requestContext)
            return
        }

        // we need to make sure that the request context is available in the coroutine
        val requestContextRef = LazyValue { requestContext }
        coroutineScope.launch(Dispatchers.IO) {
            // this is a bit of a hack, but it's the only way to get the request context into the coroutine
            // we're using a lazy value to avoid having to pass the request context around
            val ctx = requestContextRef.get()
            ctx.suspend()
            try {
                delegate.handle(ctx)
            } catch (t: Throwable) {
                ctx.handleException(t, true)
            }
            ctx.resume()
        }
    }
}

