import org.jboss.resteasy.reactive.server.spi.ServerRestHandler

class CoroutineMethodProcessor : ServerRestHandler {
    override fun handle(requestContext: ResteasyReactiveRequestContext) {
        // This is a no-op handler that just ensures the request context is properly set up for coroutine processing
        // The actual handling is done by CoroutineInvocationHandler
        requestContext.next()
    }
}

