 * Text protocol backend handler factory.
 *
 * @author zhangliang
 */
public final class TextProtocolBackendHandlerFactory {

    /**
     * Create new instance of backend handler.
     *
     * @param sqlStatement sql statement
     * @param databaseType database type
     * @return backend handler
     */
    public static BackendHandler create(final SQLStatement sqlStatement, final DatabaseType databaseType) {
        if (databaseType == DatabaseType.POSTGRESQL) {
            return new PostgreSQLBackendHandler(sqlStatement);
        }
        return new DefaultBackendHandler(sqlStatement);
    }
}

