import io.micrometer.core.instrument.Timer
import io.micrometer.core.instrument.tags.Tag
import io.micrometer.core.instrument.tags.Tags
import org.slf4j.LoggerFactory
import java.util.*

/**
 * Utility class for creating Micrometer metrics.
 */
object DgsGraphQLMetricsInstrumentationUtils {

    private val logger = LoggerFactory.getLogger(DgsGraphQLMetricsInstrumentationUtils::class.java)

    /**
     * Creates a [Timer.Sample] for the given [GqlMetric] and [GqlTag] values.
     *
     * @param metric The metric to create a sample for.
     * @param tags The tags to apply to the timer.
     * @return A [Timer.Sample] for the given metric and tags.
     */
    fun createSample(metric: DgsMetrics.GqlMetric, tags: Iterable<Tag>): Timer.Sample {
        logger.debug("Creating sample for metric {} with tags {}", metric.key, tags)
        return Timer.start().tag("gql.metric", metric.key).tags(tags)
    }

    /**
     * Creates a [Tag] for the given [GqlTag] and value.
     *
     * @param tag The tag to create.
     * @param value The value to apply to the tag.
     * @return A [Tag] for the given tag and value.
     */
    fun createTag(tag: DgsMetrics.GqlTag, value: String): Tag {
        return Tag.of(tag.key, value)
    }

    /**
     * Creates a [Tags] object from the given [GqlTag] and value pairs.
     *
     * @param tags The tag-value pairs to create tags from.
     * @return A [Tags] object for the given tag-value pairs.
     */
    fun createTags(tags: Map<DgsMetrics.GqlTag, String>): Tags {
        return Tags.of(*tags.map { Tag.of(it.key, it.value) }.toTypedArray())
    }
}

