 * Renders types in a way that's suitable for code generation.
 */
internal class TypeRenderer(
  private val typeResolver: TypeResolver,
  private val isDeclaration: Boolean = false
) {
  fun render(type: TypeName): String {
    return when (type) {
      is ClassName -> renderClassName(type)
      is ParameterizedTypeName -> renderParameterizedTypeName(type)
      is ArrayTypeName -> renderArrayTypeName(type)
      is WildcardTypeName -> renderWildcardTypeName(type)
      is TypeVariableName -> renderTypeVariableName(type)
      else -> type.toString()
    }
  }

  private fun renderClassName(className: ClassName): String {
    return if (isDeclaration) {
      className.canonicalName
    } else {
      className.simpleNames.joinToString(".")
    }
  }

  private fun renderParameterizedTypeName(type: ParameterizedTypeName): String {
    val builder = StringBuilder()
    builder.append(render(type.rawType))
    if (type.typeArguments.isNotEmpty()) {
      builder.append("<")
      builder.append(type.typeArguments.joinToString(", ") { render(it) })
      builder.append(">")
    }
    return builder.toString()
  }

  private fun renderArrayTypeName(type: ArrayTypeName): String {
    return when (type.componentType) {
      is ClassName -> "${render(type.componentType)}[]"
      else -> type.toString()
    }
  }

  private fun renderWildcardTypeName(type: WildcardTypeName): String {
    return when {
      type.upperBounds.isNotEmpty() -> {
        val bound = type.upperBounds[0]
        if (bound == ANY) "out $bound" else "? extends ${render(bound)}"
      }
      type.lowerBounds.isNotEmpty() -> {
        val bound = type.lowerBounds[0]
        "? super ${render(bound)}"
      }
      else -> "?"
    }
  }

  private fun renderTypeVariableName(type: TypeVariableName): String {
    return type.name
  }
}

