    implements WebSocketConfigurer, WebSocket, WebSocketCallback<Void> {

  private final NettyContext ctx;
  private final Channel channel;
  private final boolean dispatch;
  private OnConnect onConnectCallback;
  private OnMessage onMessageCallback;
  private OnClose onCloseCallback;
  private OnError onErrorCallback;
  private String key;

  public NettyWebSocket(NettyContext ctx, Channel channel) {
    this.ctx = ctx;
    this.channel = channel;
    this.dispatch = !ctx.isInIoThread();
    this.key = ctx.getRoute().getPattern();
  }

  @Override public void handlerAdded(ChannelHandlerContext ctx) throws Exception {
    super.handlerAdded(ctx);
    this.ctx.setChannel(ctx.channel());
  }

  @Override public void handlerRemoved(ChannelHandlerContext ctx) throws Exception {
    super.handlerRemoved(ctx);
    if (onCloseCallback != null) {
      try {
        onCloseCallback.onClose(this, new WebSocketCloseStatus(1006, "Connection closed"));
      } catch (Throwable x) {
        ctx.fireExceptionCaught(x);
      }
    }
  }

  @Override public void channelRead0(ChannelHandlerContext ctx, WebSocketFrame frame) throws Exception {
    if (frame instanceof TextWebSocketFrame) {
      onMessage(ctx, ((TextWebSocketFrame) frame).text());
    } else if (frame instanceof BinaryWebSocketFrame) {
      onMessage(ctx, ((BinaryWebSocketFrame) frame).content().nioBuffer());
    } else if (frame instanceof CloseWebSocketFrame) {
      onClose(ctx, (CloseWebSocketFrame) frame);
    } else if (frame instanceof PingWebSocketFrame) {
      ctx.writeAndFlush(new PongWebSocketFrame(frame.content().retain()));
    } else {
      ctx.writeAndFlush(new TextWebSocketFrame("Unsupported frame type: " + frame.getClass().getSimpleName()));
    }
  }

  private void onClose(ChannelHandlerContext ctx, CloseWebSocketFrame frame) {
    if (onCloseCallback != null) {
      try {
        onCloseCallback.onClose(this, new WebSocketCloseStatus(frame.statusCode(), frame.reasonText()));
      } catch (Throwable x) {
        ctx.fireExceptionCaught(x);
      }
    }
    ctx.close();
  }

