import com.netflix.graphql.dgs.metrics.DgsMetrics
import io.micrometer.core.instrument.Tag
import io.micrometer.core.instrument.Tags
import org.slf4j.LoggerFactory
import java.util.*

/**
 * Utility class for creating tags for GraphQL metrics.
 */
object DgsGraphQLMetricsInstrumentationUtils {

    private val logger = LoggerFactory.getLogger(DgsGraphQLMetricsInstrumentationUtils::class.java)

    /**
     * Creates tags for a GraphQL resolver metric.
     * The tags include the field name, path, and outcome.
     *
     * @param fieldName The name of the field being resolved.
     * @param path The path of the field being resolved.
     * @param outcome The outcome of the resolver execution.
     * @return A [Tags] object containing the tags for the resolver metric.
     */
    fun createResolverTags(
        fieldName: String,
        path: String,
        outcome: String
    ): Tags {
        return Tags.of(
            Tag.of(DgsMetrics.GqlTag.FIELD.key, fieldName),
            Tag.of(DgsMetrics.GqlTag.PATH.key, path),
            Tag.of(DgsMetrics.GqlTag.OUTCOME.key, outcome)
        )
    }

    /**
     * Creates tags for a GraphQL query metric.
     * The tags include the path and outcome.
     *
     * @param path The path of the query.
     * @param outcome The outcome of the query execution.
     * @return A [Tags] object containing the tags for the query metric.
     */
    fun createQueryTags(
        path: String,
        outcome: String
    ): Tags {
        return Tags.of(
            Tag.of(DgsMetrics.GqlTag.PATH.key, path),
            Tag.of(DgsMetrics.GqlTag.OUTCOME.key, outcome)
        )
    }
}

