package io.trino.filesystem;

import java.net.URI;
import java.net.URISyntaxException;
import java.util.Objects;

import static java.util.Objects.requireNonNull;

public class Location
{
    private final URI uri;
    private final String path;

    public Location(String location)
    {
        this(location, false);
    }

    public Location(String location, boolean allowRelativePath)
    {
        requireNonNull(location, "location is null");
        try {
            URI uri = URI.create(location);
            if (uri.getScheme() == null) {
                if (allowRelativePath) {
                    this.uri = new URI("file", null, location, null);
                }
                else {
                    this.uri = new URI("file", null, new java.io.File(location).getAbsolutePath(), null);
                }
            }
            else {
                this.uri = uri;
            }
        }
        catch (URISyntaxException e) {
            throw new IllegalArgumentException("Invalid location: " + location, e);
        }
        this.path = normalizePath(uri.getPath());
    }

    public Location(URI uri)
    {
        this.uri = requireNonNull(uri, "uri is null");
        this.path = normalizePath(uri.getPath());
    }

    public URI getUri()
    {
        return uri;
    }

    public String getPath()
    {
        return path;
    }

    private static String normalizePath(String path)
    {
        if (path == null || path.isEmpty()) {
            return path;
        }
        // Remove duplicate slashes and handle path normalization
        return path.replaceAll("/+", "/").replaceAll("/$", "");
    }

    @Override
    public boolean equals(Object o)
    {
        if (this == o) {
            return true;
        }
        if (o == null || getClass() != o.getClass()) {
            return false;
        }
        Location location = (Location) o;
        return Objects.equals(uri, location.uri);
    }

    @Override
    public int hashCode()
    {
        return Objects.hash(uri);
    }

    @Override
    public String toString()
    {
        return uri.toString();
    }
}
