public class TrinoS3FileSystem
        extends AbstractTrinoS3FileSystem
{
    private final HdfsEnvironment hdfsEnvironment;
    private final AmazonS3 amazonS3;
    private final String bucketName;
    private final String keyPrefix;

    public TrinoS3FileSystem(
            HdfsEnvironment hdfsEnvironment,
            AmazonS3 amazonS3,
            Location location)
    {
        this.hdfsEnvironment = requireNonNull(hdfsEnvironment, "hdfsEnvironment is null");
        this.amazonS3 = requireNonNull(amazonS3, "amazonS3 is null");
        URI uri = location.getUri();
        this.bucketName = uri.getHost();
        this.keyPrefix = uri.getPath().substring(1); // Remove leading slash
    }

    @Override
    public boolean exists(Location location)
            throws IOException
    {
        try {
            S3ObjectSummary objectSummary = getObjectSummary(location);
            return objectSummary != null;
        }
        catch (AmazonS3Exception e) {
            if (e.getStatusCode() == 404) {
                return false;
            }
            throw new IOException("Failed to check if location exists: " + location, e);
        }
    }

    @Override
    public InputStream newInputStream(Location location)
            throws IOException
    {
        S3Object s3Object = amazonS3.getObject(
                bucketName,
                keyPrefix + location.getPath());
        return s3Object.getObjectContent();
    }

    @Override
    public List<Location> listLocations(Location location)
            throws IOException
    {
        String prefix = keyPrefix + location.getPath();
        if (!prefix.endsWith("/")) {
            prefix += "/";
        }

        ListObjectsV2Request request = new ListObjectsV2Request()
                .withBucketName(bucketName)
                .withPrefix(prefix)
                .withMaxKeys(1000);

        ListObjectsV2Result result = amazonS3.listObjectsV2(request);
        return result.getObjectSummaries().stream()
                .map(summary -> Location.of("s3://" + bucketName + "/" + summary.getKey()))
                .collect(ImmutableList.toImmutableList());
    }

    private S3ObjectSummary getObjectSummary(Location location)
    {
        String key = keyPrefix + location.getPath();
        try {
            return amazonS3.listObjectsV2(bucketName, key).getObjectSummaries().stream()
                    .filter(summary -> summary.getKey().equals(key))
                    .findFirst()
                    .orElse(null);
        }
        catch (Exception e) {
            return null;
        }
    }

