from django.db.migrations.operations.base import Operation
from django.db.migrations.state import ModelState
from django.db.migrations.utils import (
    field_references, get_references, resolve_relation,
)
from django.db.models import Field
from django.utils.functional import cached_property

from .base import Operation

class FieldOperation(Operation):
    def __init__(self, name, field):
        self.name = name
        self.field = field

    def field_references(self, model, name):
        return field_references(self.field, model, name)

    @cached_property
    def field_name(self):
        return self.field.name

    def references_field(self, model, name):
        return self.field_references(model, name)

    def reduce(self, operation, app_label):
        return super().reduce(operation, app_label) or (
            isinstance(operation, FieldOperation) and
            self.name == operation.name and
            self.field_name == operation.field_name
        )

class AddField(FieldOperation):
    def __init__(self, model_name, name, field, preserve_default=True):
        super().__init__(name, field)
        self.model_name = model_name
        self.preserve_default = preserve_default

    def state_forwards(self, app_label, state):
        # Add the field to the model state
        model_state = state.models[app_label, self.model_name]
        model_state.fields[self.name] = self.field

    def database_forwards(self, app_label, schema_editor, from_state, to_state):
        # Add the field to the database
        model = to_state.apps.get_model(app_label, self.model_name)
        if not model._meta.db_table:
            return
        field = self.field
        if field.remote_field and field.remote_field.model:
            field = field.clone()
            field.remote_field.model = to_state.apps.get_model(
                field.remote_field.model._meta.app_label,
                field.remote_field.model._meta.model_name,
            )
        schema_editor.add_field(
            model,
            field,
        )

    def database_backwards(self, app_label, schema_editor, from_state, to_state):
        model = from_state.apps.get_model(app_label, self.model_name)
        if not model._meta.db_table:
            return
        schema_editor.remove_field(model, self.field)

    def describe(self):
        return "Add field %s to %s" % (self.name, self.model_name)

    @property
    def migration_name_fragment(self):
        return "%s_%s" % (self.model_name, self.name)

    def references_model(self, model_name):
        return self.model_name == model_name

    def references_field(self, model_name, field_name):
        return self.model_name == model_name and self.name == field_name

class RemoveField(FieldOperation):
    def __init__(self, model_name, name):
        super().__init__(name, None)
        self.model_name = model_name

    def state_forwards(self, app_label, state):
        # Remove the field from the model state
        model_state = state.models[app_label, self.model_name]
        del model_state.fields[self.name]

    def database_forwards(self, app_label, schema_editor, from_state, to_state):
        model = from_state.apps.get_model(app_label, self.model_name)
        if not model._meta.db_table:
            return
        schema_editor.remove_field(model, from_state.apps.get_model(app_label, self.model_name)._meta.get_field(self.name))

    def database_backwards(self, app_label, schema_editor, from_state, to_state):
        model = to_state.apps.get_model(app_label, self.model_name)
        if not model._meta.db_table:
            return
        field = self.field
        if field.remote_field and field.remote_field.model:
            field = field.clone()
            field.remote_field.model = to_state.apps.get_model(
                field.remote_field.model._meta.app_label,
                field.remote_field.model._meta.model_name,
            )
        schema_editor.add_field(model, field)

    def describe(self):
        return "Remove field %s from %s" % (self.name, self.model_name)

