    """
    The base storage class for messages.
    """

    def __init__(self, request, *args, **kwargs):
        self.request = request
        self._queued_messages = []
        self._loaded_messages = []
        self._loaded = False

    def _get(self):
        if not self._loaded:
            self._load()
        return self._loaded_messages

    def _set(self, messages):
        self._queued_messages = messages

    def __len__(self):
        return len(self._get())

    def __iter__(self):
        return iter(self._get())

    def __contains__(self, item):
        return item in self._get()

    def __repr__(self):
        return f'<{self.__class__.__name__}: {len(self)} messages>'

    def add(self, level, message, extra_tags=''):
        """
        Add a message to the storage.
        """
        self._queued_messages.append(Message(level, message, extra_tags))

    def _load(self):
        """
        Load messages from storage.
        """
        raise NotImplementedError('subclasses must implement _load()')

    def _store(self, messages, response):
        """
