        """
        Get a message from the storage.
        """
        if level is None and message is None and extra_tags is None:
            return self._get()
        messages = []
        for msg in self._get():
            if (level is None or msg.level == level) and \
               (message is None or msg.message == message) and \
               (extra_tags is None or msg.extra_tags == extra_tags):
                messages.append(msg)
        return messages

    def get_by_id(self, id):
        """
        Get a message by its ID.
        """
        for msg in self._get():
            if msg.id == id:
                return msg
        return None

