class ReverseManyToOneDescriptor:
    """
    Accessor to the related object on the reverse side of a many-to-one relation.

    In the example::

        class Model(models.Model):
            field = models.ForeignKey(OtherModel, on_delete=models.CASCADE)

    ``Model.field`` is a ``ReverseManyToOneDescriptor``.
    """

    def __init__(self, rel):
        self.rel = rel
        self.field_name = rel.field.name

    def __get__(self, instance, owner):
        if instance is None:
            return self
        return ReverseManyToOneNullHandler(instance, self.rel, self.field_name)

    def __set__(self, instance, value):
        if instance is None:
            raise AttributeError("%s must be accessed via instance" % self.field_name)

        # If the reverse relation already exists, we can just use it.
        if hasattr(instance, self.field_name):
            return

        # Otherwise, we need to create a new one.
        manager = ReverseManyToOneManager(
            instance,
            self.rel,
            self.field_name,
        )
        setattr(instance, self.field_name, manager)


