class ArrayField(PostgresField):
    description = _("Array")
    default_error_messages = {
        "item_invalid": _("Item %(value)r is not of type %(type)s."),
        "list": _("Value must be a list or tuple."),
        "min_length": _("Ensure array contains no fewer than %(limit_value)d items."),
        "max_length": _("Ensure array contains no more than %(limit_value)d items."),
    }

    def __init__(self, base_field, size=None, **kwargs):
        self.base_field = base_field
        self.size = size
        super().__init__(**kwargs)

    def deconstruct(self):
        name, path, args, kwargs = super().deconstruct()
        kwargs['base_field'] = self.base_field
        if self.size is not None:
            kwargs['size'] = self.size
        return name, path, args, kwargs

    def validate(self, value, model_instance):
        super().validate(value, model_instance)
        if value:
            for index, item in enumerate(value):
                try:
                    self.base_field.validate(item, model_instance)
                except exceptions.ValidationError as e:
                    raise prefix_validation_error(
                        e,
                        prefix=_("Item %(index)d: "),
                        index=index,
                    )

    def clean(self, value, model_instance):
        value = super().clean(value, model_instance)
        if value:
            return [self.base_field.clean(item, model_instance) for item in value]
