        return [self.base_field.get_db_prep_value(item, connection, prepared) for item in value]

    def get_db_prep_save(self, value, connection):
        return self.get_db_prep_value(value, connection)

    def value_from_object(self, obj):
        value = super().value_from_object(obj)
        if value:
            return [self.base_field.value_from_object(item) for item in value]
