    This module contains the core outcome handling of pytest.
    It handles the different types of outcomes (pass, fail, error, skip, xfail)
    and the different ways they can be triggered.
"""

import sys
from typing import TYPE_CHECKING

if TYPE_CHECKING:
    from _pytest.nodes import Item

class OutcomeException(Exception):
    """Base class for all outcome exceptions."""

    def __init__(self, msg: str = "", *args) -> None:
        super().__init__(msg, *args)
        self.msg = msg

class Skipped(OutcomeException):
    """Raised when a test is skipped."""

    def __init__(self, msg: str = "", *args) -> None:
        super().__init__(msg, *args)
        self.msg = msg

class Failed(OutcomeException):
    """Raised when a test fails."""

    def __init__(self, msg: str = "", *args) -> None:
        super().__init__(msg, *args)
        self.msg = msg

class XFailed(OutcomeException):
    """Raised when a test is expected to fail."""

    def __init__(self, msg: str = "", *args) -> None:
        super().__init__(msg, *args)
        self.msg = msg

class XPassed(OutcomeException):
    """Raised when a test was expected to fail but passed."""

    def __init__(self, msg: str = "", *args) -> None:
        super().__init__(msg, *args)
        self.msg = msg

