def _evalf_pow(self, prec, options):
    """Evaluate power expressions in floating point."""
    from sympy.core.power import Pow
    from sympy.core.mul import Mul
    from sympy.core.add import Add

    base, exp = self.args
    base = base.evalf(prec, options)
    exp = exp.evalf(prec, options)

    # Handle special cases
    if exp.is_Integer:
        return base**exp
    elif exp.is_Rational and exp.p == 1:
        # For 1/n roots, use root function
        return base**(1/exp.q)
    else:
        # General case: use standard power
        return base**exp

