    def dumps(self, obj):
        return json.dumps(
            obj,
            separators=(',', ':'),
            cls=MessageEncoder,
        ).encode('latin-1')

    def loads(self, data):
        return json.loads(data.decode('latin-1'), cls=MessageDecoder)

